import axios from 'axios';
import React, { useEffect } from 'react'
import { useParams } from 'react-router-dom'
import { useContextGlobal } from '../../utils/global.context';
import './detail.css';
//Este componente debera ser estilado como "dark" o "light" dependiendo del theme del Context

const Detail = () => {

  const {id}  = useParams();
  const { state, dispatch } = useContextGlobal();
  const url = `https://jsonplaceholder.typicode.com/users/${id}`;

  useEffect(() => {
    axios(url)
      .then((res) => {
        dispatch({ type: 'SET_DETAIL', payload: res.data });
      })
      .catch((error) => {
        console.error('Error fetching dentist details:', error);
      });
  }, [url, dispatch]);
  
  return (
    <div className={`detail ${state.theme}`}>
      <h1>Dentista {state.dentist && state.dentist.id} </h1>
      {state.dentist && (
        <>
          <p>Username: {state.dentist.name}</p>
          <p>Email: {state.dentist.email}</p>
          <p>Phone: {state.dentist.phone}</p>
          <p>Website: {state.dentist.website}</p>
        </>
      )}
      </div>
  )
}

export default Detail
