import React, { useEffect, useState, u} from 'react'
import { useParams } from 'react-router-dom'
import { useContextGlobal } from '../Components/utils/global.context';


//Este componente debera ser estilado como "dark" o "light" dependiendo del theme del Context

const Detail = () => {
  const { id } = useParams()
  const { state } = useContextGlobal()
  const [dentist, setDentist] = useState(null)

  useEffect(() => {
    const dentist = state.dentists.find(dentist => dentist.id === Number(id)) 
    if(dentist) return setDentist(dentist)
  }, [state.dentists, id])

  if(!dentist) return <p className='Detail NotFound'>Dentist Not Found</p>

  return (
    <div className='Detail'>
      <h1>Detalle Dentista {dentist.id}</h1>
      <p>Name: {dentist.name}</p>
      <p>Email: {dentist.email}</p>
      <p>Phone: {dentist.phone}</p>
      <p>Website: {dentist.website}</p>
    </div>
  )
}
export default Detail
