import reducer, { initialState, SET_DATA_API, SET_THEME, ADD_FAV, REMOVE_FAV, TOGGLE_THEME} from '../reducer/reducer';
import React, { createContext, useReducer, useContext, useEffect } from 'react';
import axios from 'axios';

export const ContextGlobal = createContext();

 initialState = {
  SET_THEME: 'light',
  ADD_FAV: JSON.parse(localStorage.getItem('favlist')) ? JSON.parse(localStorage.getItem('favlist')): [],
  SET_DATA_API: [],
  REMOVE_FAV: JSON.parse(localStorage.getItem('favlist')) ? JSON.parse(localStorage.getItem('favlist')): [],
  TOGGLE_THEME: { theme: "light", data: [] }
}

const ContextProvider = ({ children }) => {

  const [state, dispatch] = useReducer(reducer, initialState);
  const url = 'https://jsonplaceholder.typicode.com/users';
  
  useEffect(() => {
    axios.get(url).then(res => dispatch({type: SET_DATA_API, payload: res.data}));
  }, []);

  useEffect(() => {
    localStorage.setItem('favlist', JSON.stringify(state.favlist));
  })

  return (
    <ContextGlobal.Provider value={{state , dispatch}}>
      {children}
    </ContextGlobal.Provider>
  );
};

export default ContextProvider;

export const useContextGlobal = () => useContext(ContextGlobal);
